import React, { useState, useEffect } from 'react'
import PropTypes from 'prop-types'
import M from 'materialize-css/dist/js/materialize.min.js'
import { connect } from 'react-redux'
import { updateLog } from '../../actions/logActions'

const EditLogModal = ({ current, updateLog }) => {

    const [message, setMessage] = useState('')
    const [attention, setAttention] = useState(false)
    const [tech, setTech] = useState('')

    useEffect(() => {
        if(current){
            setMessage(current.message)
            setAttention(current.attention)
            setTech(current.tech)
        }
    },[current])

    const onSubmit = () => {
        if(message === '' || tech === '' ){
            M.toast({ html: 'Please Enter a Message and a Tech'})
        }else{
            console.log(message, tech, attention)
            //Clear Fields
            setMessage('')
            setTech('')
            setAttention(false)
        }
    }
    
    return (
        <div id="edit-log-modal" className="modal" style={modalStyle}> 
            <div className="modal-content">
                <h4>Enter System Log</h4>
                <div className="row">
                    <div className="input-field">
                        <input 
                            type="text"
                            name="message" 
                            value={message} 
                            onChange={(e) => setMessage(e.target.value)}
                        />
                        <label htmlFor="message" className="active">Log Message</label>
                    </div>
                </div>
                <div className="row">
                    <div className="input-field">
                        <select className="browser-default" name="tech" value={tech} onChange={(e) => setTech(e.target.value)}>
                            <option value="" disabled>Select Technician</option>
                            <option value="Vader Smith" >Vader Smith</option>
                            <option value="Tom Jones" >Tom Jones</option>
                            <option value="Huey Lewis">Huey Lewis</option>
                        </select>
                    </div>
                </div>
                <div className="row">
                    <div className="input-field">
                        <p>
                            <label>
                                <input 
                                type="checkbox" 
                                className="filled-in"
                                checked={attention} 
                                value={attention} 
                                onChange={(e) => setAttention(!attention)} />
                                <span>Needs Attention</span>
                            </label>
                        </p>
                    </div>
                </div>
            </div>
            <div className="modal-footer">
                <a href="#!" onClick={onSubmit} className="modal-close waves-effect blue waves-light btn">Enter</a>
            </div>
        </div>
    )
}

const modalStyle = {
    width: '75%',
    height: '75%',
}

EditLogModal.propTypes = {
    current: PropTypes.object,
    updateLog: PropTypes.func.isRequired,
}

const mapStateToProps = (state) => ({
    current: state.log.current
})


export default connect (mapStateToProps, { updateLog }) (EditLogModal)
